INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('机器学习（python开发）', '经验1-3年', '本科及以上', '不需要融资', '海万科技', '移动互联网,金融', '深圳', '10k-20k', '5780692', '旅游,年终奖,过节费,环境好', '福田区', '图像处理,语音识别,语音合成,机器学习', '岗位职责：1. 对海量业务数据进行处理和分析，发现和跟踪其中的问题；2. 从业务和产品的角度出发，利用数据来发现产品、系统或是业务的瓶颈，并提出优化的方案；3. 辅助自然语言处理核心算法或功能模块的设计、开发等端到端开发任务；4. 运用知识图谱中文文本挖掘算法，创建领域知识图谱，负责基于知识图谱的检索优化。任职资格：1. 本科及以上学历，在自然语言处理、智能问答等人工智能技术领域有相关应用经验；2. 具备机器学习、深度学习的知识基础，熟悉主流算法，了解业界最新理论和技术进展；3. 熟练使用Python/Java/Shell等语言进行数据处理和分析；4. 了解Tensorflow/Mxnet/Caffe/Theano。');
INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('机器学习', '经验5-10年', '本科及以上', 'A轮', 'DataHunter', '数据服务,企业服务', '北京', '33K-61K', '4457349', '五道口,数据可视化,技术大牛,人工智能', '海淀区', '大数据,算法,数据挖掘', '【你要负责的事】1、基于机器学习/深度学习进行公司大数据分析相关产品的研发；2、利用机器学习/深度学习建立业务预测模型，指导客户决策；3、进行大数据量的用户行为数据的处理、清洗和挖掘，并持续迭代优化推荐策略；4、从数据中发现现有系统和算法的不足，提出改进的算法并推动实现5、追踪业内前沿技术，结合业务特点，探索将前沿的算法技术应用于实际业务【我们需要你】1、统招本科及以上学历，计算机、统计学、数学等背景优先；2、有5年以上大数据相关研发经验，2年以上机器学习经验；3、精通Python语言及相关的开发工具，熟悉特征工程，熟悉数据处理的基础件；4、较好的数据挖掘处理经验，熟悉常用的机器学习算法；5、有较多的程序开发经验，对数据结构和算法知识应用熟练，有大数据处理经验者更佳；6、良好的沟通和推动能力，主动性强。');
INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('新媒体运营(J11643)', '经验1-3年', '本科及以上', 'D轮及以上', '作业帮', '移动互联网,教育', '北京', '10k-15k', '5552583', '年底双薪 七险一金 三餐水果', '海淀区', '教育', '工作职责:1、运营K12教育类自媒体矩阵，包括但不限于微信、微博、头条号、网易号、企鹅号、一点号、大鱼号、抖音等短视频平台；2、能独立完成日常内容策划和产出，对内容的引流和转化结果负责；3、紧跟新媒体发展趋势，积极探索不同平台运营模式；4、跟踪运营效果，分析数据，及时反馈和优化运营模式；5、对竞品的动向进行分析跟踪，及时优化运营方向。任职资格:1、  深挖用户需求，写得了简单粗暴的、打动人的好文案，能成功通过文案，安利别人买东西；2、  学习能力强，善于模仿，网感好，创意优，审美佳，执行力强，有较强新闻、热点敏感性；3、  脑洞大、熟悉短视频套路，能策划高传播量的短视频内容；4、  有责任心，细心，具备一定沟通能力；5、  会视频剪辑、PS、表情包制作技能加分，抖音、B站等短视频平台深度用户加分。');
INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('NLP开发工程师', '经验1-3年', '硕士及以上', 'B轮', '达观数据', '人工智能', '上海', '15k-25k', '5466125', '专注NLP,技术牛人多,准独角兽,期权', '浦东新区', '工具软件,大数据,后端,NLP,Python,软件开发', '职位描述：1、参与研究语义建模、自然语言理解、机器阅读、文本分类等NLP关键前沿技术的研发工作2、参与NLP产品化进程，探索深度学习（Deep Learning）在自然语言处理领域中的应用，实现切实有效的深度学习模型3、参与构建各种机器学习和文本数据挖掘的实用化工具职位要求：1、计算机、统计、数学及相关专业硕士及以上学历2、1年以上自然语言处理算法建模与工程开发相关工作经验，理论知识扎实3、熟悉常用的编程语言（如Python、Java等），有扎实的编程能力4、思路清晰，主动性强，有很强的自我驱动力5、责任心强，能承受工作压力，良好的沟通交流能力，能迅速融入团队');
INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('深度学习算法工程师', '经验不限', '本科及以上', '不需要融资', '众链科技', '移动互联网,硬件', '上海', '15k-30k', '5734305', '前景好,有潜力', '浦东新区', '深度学习,图像处理', '岗位职责： 1.开展机器学习/深度学习等相关领域研究和开发工作：紧跟业界算法发展，运用于公司产品开发；2.负责深度学习算法框架搭建（模型实现、模型压缩、模型优化），嵌入式移植优化落地及维护。包括机器学习、2D/3D图像处理等的算法和系统研发3.支持公司相关产品深度学习相关研究任职要求：1. 深度学习/模式识别/图像处理等相关专业硕士及以上学历；2. 至少2年及以上计算机视觉/图像处理相关工作经验；至少1年及以上深度学习/机器学习相关工作经验。3. 具备优秀的执行力，足够的责任心，对新技术和方法足够敏锐，能及时跟踪并实现前沿算法。4. 在数学、深度学习、模式识别、计算机视觉等方面有扎实研究基础，在2D/3D图像检测识别方面相关项目经验；对当下主流深度学习算法，如YOLO, SSD，RCNN，DenseNet, MobileNet, 以及对CNN/DNN/GAN/RNN/VAE/ITree等相关算法有深入了解与应用。5. 有过硬的编码能力，熟悉并能运用tensorflow, caffe, pytorch等至少一种深度学习框架，python或C及Linux系统。6. 熟悉 Linux 环境开发，精通 C/C++, 精通数据结构和基础算法，工程能力强；7. 具有人脸相关经验者优先；具有移动端深度模型优化经验者优先；');
INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('深度学习算法开发工程师', '经验1年以下', '本科及以上', 'C轮', '旷视MEGVII', '移动互联网,硬件', '北京', '20k-40k', '5810724', 'AI独角兽,黑科技,福利好,发展空间大。', '海淀区', '人工智能,算法,计算机视觉', '工作职责：1. 负责核心算法的产品化，包括封装、编译、优化等工作；2. 提升代码性能和算法稳定性；3. 设计自动化的算法封装流程，提高算法的产品化迭代效率。任职资格：1、熟练 C/C++ 编程，代码风格良好；2、了解bash、python等脚本语言3、熟悉系统优化或算法优化方法，如多线程，DSP，neon，opencl等。4、有以下经验者优先：-有计算机视觉基础，了解OpenCV；-Android / iOS 交叉编译经验；-熟悉 Android Framework，有NDK开发经验；-接口设计经验和移动端 SDK 持续集成经验；熟悉并行计算软件开发，熟悉SIMD,SIMT等加速原理。');
INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('深度学习实习生', '经验应届毕业生', '本科及以上', 'B轮', '思核科技', '人工智能', '上海', '7k-8k', '4887547', '技术驱动,高薪,大牛团队,氛围好', '徐汇区', '机器学习,深度学习,算法,计算机视觉', '参与下列一项或多项工作：1、探索与实现深度学习领域前沿研究，搭建原型与算法评估验证；2、结合实际场景，研发与优化深度学习算法，包括神经网络模型设计/网络训练与测试/数据生成与维护等；3、参与技术的工程化工作，推动算法在现实问题中的落地和迭代。 要求：1、本科及以上学历，强大的C++/Python编程能力，优秀的算法、数据结构知识；2、熟悉Tensorflow或类似的机器学习框架，熟悉CNN和RNN至少其中之一；3、强烈技术热情和学习欲望，快速掌握新知识；4、出色的解决问题能力，乐于技术分享和讨论。 加分项：1、在机器学习领域A类会议或期刊上发表过Deep Learning相关领域论文；2、有机器学习/计算机视觉/机器人领域实际研发经验；3、NOI、ACM World Final、超算、Robocon等竞赛获奖经验。');
INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('深度学习算法工程师', '经验3-5年', '硕士及以上', 'C轮', '旷视MEGVII', '移动互联网,硬件', '北京', '30k-50k', '5718515', '人工智能', '海淀区', '计算机视觉,图像处理,人工智能,机器学习', '工作职责：1、负责深度学习及机器学习相关算法的开发和应用；2、负责深度学习相关技术栈及算法的预研和选型；3、 负责算法相关的数据处理。任职资格：1、熟悉机器学习的基础理论和常用算法；2、两年以上深度学习相关的工作经验，熟悉tensorflow，pytorch等深度学习框架；3、熟悉linux系统，熟悉Python语言；4、熟悉逻辑回归、随机森林、决策树、贝叶斯、SVM等基础分类算法；5、熟悉CNN、RNN等人工智能算法及其原理；6、熟悉有监督学习、无监督学习、强化学习等原理及实现；7、对数据敏感，具有良好的沟通能力和团队合作精神，具有强烈的责任心和钻研精神。');
INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('SDK产品经理', '经验5-10年', '本科及以上', 'C轮', '旷视MEGVII', '移动互联网,硬件', '北京', '30k-50k', '5085792', 'SDK,算法,泛安防', '海淀区', '产品设计,用户研究,智能硬件', '工作职责:1、负责SDK产品规划、设计和项目执行； 2、分析SDK用户需求，分析竞争对手动态和市场动态，规划路线图，提出发展需求或改进意见； 3、协调技术开发人员，跟踪功能开发进度，完成功能的开发、测试、版本管理，评审发布，功能上线等相关工作； 4、对SDK优化需求进行版本管理，上线后改进等相关工作。任职资格:1、本科以上学历，5年以上互联网行业产品/项目管理经验，有算法背景优先；2、逻辑强，产品感优秀，能够深度分析、理解用户需求，同理心好、不断迭代自己；3、负责任、学习能力强，需要很好的适应能力与抗压能力，能够融入快节奏的产品团队；4、对工作充满激情，具有强烈的创新精神，富于责任心，能承受工作压力。');
