INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('深度学习工程师（模型优化方面）', '经验1-3年', '本科及以上', 'A轮', '星舆科技', '移动互联网,数据服务', '广州', '15k-25k', '5532573', '高速成长,扁平化,行业前景佳,科研氛围', '天河区', '地图,云计算,深度学习,图像处理', '工作职责：1、负责不同深度学习训练平台的调研与跟进，研究模型训练和模型压缩的方法。2、使用深度学习方法解决图像中的目标检测、分类、识别、分割、语义理解等问题。 招聘要求：1、计算机、自动化、数学等相关专业研究生及以上学历；2、掌握数学图像处理常用方法；3、对深度学习理论有深刻理解，对卷积神经网络有良好的理论和实操经验。4、至少对于以下领域之一有良好的实际项目经验积累：使用深度学习解决图像中的目标检测和识别、图像分类、区域分割、字符识别、图像内容理解等等。  5、精通c/c++、python语言，熟悉OpenCV、caffe、tensorflow等框架，有MPI、CUDA等并行程序开发经验者优先；6、具有良好的沟通能力和表达能力； 7、具有良好的分析能力、逻辑思维能力；8、有道路图像分析等相关工作经验者优先9、对于如何优化网络结构、如何减少内存使用、如何大幅降低运行时间等有经验者优先');
INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('网络优化研发工程师', '经验3-5年', '本科及以上', 'D轮及以上', '快手', '移动互联网', '北京', '15k-30k', '4910956', '技术驱动,发展空间大', '海淀区', 'C++', '岗位职责:1、为快手提供公司级的移动网络接入组件/服务，承担超大规模流量加速支持2、通过新协议的引入与定制，对底层传输协议进行深度定制优化，实现安全、可靠，以及极致性能的用户访问和交互体验3、关注业界前沿接入技术动态，负责新接入技术探索和应用任职资格:1、精通C++语言程序设计，了解Java编程2、精通多线程和网络编程，对高性能程序设计、架构有较多的工程经验 3、熟悉Linux操作系统，熟悉移动网络通信机制，对TCP/IP 和HTTP(S)协议有深刻理解4、熟悉数据结构和算法，熟悉OOD/OOP和设计模式 5、学习能力强，勤于动手，有较强分析和解决问题的能力6、具有强烈的进取心和求知欲望，善于学习和运用新知识，乐于解决具有挑战性的问题 7、具有较强的逻辑思维和责任感8、具有良好的团队合作精神和积极主动的沟通意识 9、本科及以上学历，2年以上工作经验Preferred skills：1、对Android&iOS操作系统和底层库有深入了解，熟悉系统API调用 2、对数据敏感，有大数据分析和挖掘的经验');
INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('pcg——深度学习系统后台开发...', '经验3-5年', '本科及以上', '上市公司', '腾讯', '移动互联网 ,游戏', '深圳', '20k-40k', '5461379', '平台大', '南山区', '服务器端,后端,C++,Linux/Unix', '岗位职责： 大规模分布式机器学习/深度学习系统的设计和开发； 自研分布式机器学习/深度学习框架； 模型算法与系统实现的联合优化； 跟踪资讯/搜索类内容服务业务相关的开源框架、新算法框架的技术选型及系统研发； 深入业务的流程，制定应用机器学习/深度学习技术的优化方案。岗位要求： 3年以上C++开发经验； 2年以上分布式系统设计、架构、建设经验； 具备较强的沟通能力，思维活跃，前瞻学习能力强； 较流畅的英文技术材料阅读能力； 对系统优化和算法模型应用有浓厚兴趣； 熟悉分布式机器学习/深度学习框架相关技术：TensorFlow，Caffe，MxNet等；阅读过相关源码，理解该类架构设计者优先； 具备分布式计算框架的使用和并行算法的开发经验者优先。');
INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('机器学习', '经验不限', '本科及以上', 'D轮及以上', '好大夫在线', '医疗健康', '北京', '25k-50k', '4671508', '大公司,大平台,空间大,机会多', '朝阳区', '机器学习,深度学习,图像处理', '工作职责：1. 用机器学习、文本挖掘等技术，对结构化和非结构化数据进行挖掘，发现其潜在关系，指导医疗大数据应用落地；2. 构建通用的分布式环境下机器学习系统，快速支持算法应用；3. 参与文本意图分析，包括文本分类和聚类，拼写纠错，实体识别与消歧，中心词提取，短文本理解等；4. 探索医疗AI领域。工作要求：1. 计算机/通信等相关专业，硕士及以上学历，有较好的理论基础和快速的学习能力；2. 熟练掌握TensorFlow，pyTorch，Caffe，HTS等社区开源工具中的一种及以上；3. 对常用的机器学习/深度学习算法有深入理解，例如：LR/GMM/SVM/CRF/MaxEnt/HMM/NN 等；4. 熟悉 linux 开发环境，掌握至少一门编程语言，如 C++、Python等；5. 有NLP或图像领域的工作或研究经验，搭建过实际线上系统者优先；6. 表达能力强，乐观向上，自我驱动力强，具备敏锐的洞察力和良好的抗压能力。');
