INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('机器学习（风控）', '经验不限', '硕士及以上', 'C轮', '买单侠', '移动互联网、金融', '上海', '15k-30k', '5495404', '扁平化,弹性制', '浦东新区', '机器学习', '工作职责：1、基于海量的数据基础，通过机器学习等手段，优化现有风控、反欺诈、账户安全体系，精确识别经过层层伪装的恶意行为，打造具备良好用户体验的数据产品体系；2、深入地挖掘分金融业务中各类客户属性及行为数据，量化客户类型行为特征；3、深入地分析各类风险案件，设计并实现反欺诈、账户安全场景下的风险评价模型；4、研究量化客户的关联类型、关联客户的距离。任职资格：1、硕士以上学历，较强的计算机、数学和统计学背景；2、精通logistic、随机森林、svm、xgboost、聚类等算法；3、拥有大规模数据处理和分析经验者优先；4、良好的学习意愿和学习能力，能够快速掌握工作所需技能；5、对海量数据在金融场景下的应用感兴趣');
