INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('深度学习算法工程师', '经验1-3年', '硕士及以上', 'B轮', '极目', '移动互联网,硬件', '武汉', '15k-30k', '5684747', '发展前景好 扁平管理 六险一金', '东湖新技术开发区', '汽车,深度学习,计算机视觉,图像处理', '1.负责计算机视觉算法和深度学习算法的前沿技术的研发工作；2.负责物体检测（行人、车辆、通用目标）、分类、跟踪、识别、图像理解、图像质量评估和增强，视频分析等前沿技术研发和实现。任职资格：1.图像处理、模式识别、机器学习相关专业硕士及以上学历，3年以上工作经验；2.在深度学习、统计机器学习、计算机视觉和最优化方法等方面有较深入的研究；3.熟悉物体（行人、车辆、人脸、通用目标）检测、跟踪与识别算法、熟悉图像理解（分类、分割等）、视频分析算法；4.熟悉cnn，rcnn，frcnn，boost，svm其中至少一种，并且有实际操作经验；5.动手能力强，熟练掌握C/C++、Python、Matlab语言，有较强的算法分析和实现能力；6.精通Caffe、MxNet、Tensorflow、Cuda-convnet、Torch等任一种深度学习开源框架者优先；7.熟练阅读相关英文论文和专利，并能快速编程实现。');
INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('深度/机器学习研究员', '经验不限', '硕士及以上', 'B轮', '深睿医疗', '移动互联网,医疗健康', '杭州', '20k-40k', '4722416', '大牛,人性化管理,靠谱团队,技术氛围好', '西湖区', '医疗健康,机器学习,NLP,计算机视觉,图像处理', '研究深度学习（包括各种神经网络结构与应用）或计算机视觉各个领域（目标检测识别等）中核心算法将上述核心算法和模型应用到医疗领域的现实场景中，并针对应用场景进行优化和定制等将算法高效地实现到多种不同平台和框架上，并基于对平台和框架的内部机制的理解，持续对算法和模型实现进行优化支持公司在医疗领域相关应用产品的研究、开发、文档撰写工作；岗位要求：机器学习/模式识别及其他相关领域硕士及以上学历，在深度学习领域有较深的理论或实验室研究经验良好的算法基础，扎实的编程功底，有较大型项目的开发经验能够熟练使用OpenCV，精通C/C++，Python,golang等，熟悉Linux开发环境善于解决和分析问题，富有想象力和学习能力，良好的团队合作精神，有创造性思维，有推进人工智能的理想和使命感。有以下经验者优先：具有常用的CNN/RNN模型之外深度学习相关经验，熟悉常用的深度学习软件（如caffe，pytorch等）在相关方向知名国际会议发表过论文（如CVPR，ICCV，ECCV，NIPS，ICLR，TPAMI等）参加过国际国内算法类竞赛，并取得一定名次者');
INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('算法主管-大数据方向', '经验5-10年', '硕士及以上', 'C轮', '涂鸦智能', '移动互联网,硬件', '杭州', '24k-48k', '5238998', '氛围好,团建,定期体检', '西湖区', '智能硬件,移动互联网,算法,数据挖掘,数据仓库', '岗位描述：1.从事前沿大规模人工智能与机器学习算法的研究与分布式开发;2.将算法应用到海量数据中,提升/挖掘IOT物联网大数据场景及大数据平台建设;3.提供开放平台，提供业内领先的人工智能与机器学习服务. 任职要求：1.硕士博士学位及博士后研究，计算机相关专业毕业;2.五年以上实际机器学习或深度学习应用工作经验;3.丰富的实际问题解决经验;4.有丰富C++或Java开发经验，熟练 Phython, R;5.熟悉 pattern recognition and classification 及常用统计模型和机器学习算法;6.对概率统计和机器学习基本原理有扎实理解;7.精通 machine learning, data mining, and algorithm 研发;8.熟悉Caffe/TensorFlow/MXNet/Torch/Theano等训练框架;9.熟悉Linux开发环境，熟悉开源工具，有相关领域经验者优先.');
