INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('深度学习研发工程师', '经验不限', '硕士及以上', 'C轮', '商汤科技', '移动互联网', '上海', '20k-40k', '5661563', '深入研究', '徐汇区', '深度学习', '职位描述：1、 跟进深度学习不同研究方向最新进展，包括分类、检测、分割等。2、 跟进深度学习框架及训练方法的进展，包括 Pytorch/TensorFlow/MXNet/Caffe2、大规模训练、混合精度训练等。3、 负责深度学习算法的实现和优化。职位要求：1、计算机相关专业本科及以上学历，或有相关实际项目经验。2、熟练掌握机器学习/深度学习图像处理基本方法，在分类、检测、分割中的至少一个领域有深入研究，有论文完整实现经验的优先。3、熟练掌握C++/Python/Matlab 中的至少一种编程语言。4、较强的自我学习能力和阅读文献能力。5、具有良好解决和分析问题能力，沟通能力和团队合作精神。');
