INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('高级数据分析师 (MJ001016)', '经验3-5年', '本科及以上', 'D轮及以上', '挖财', '移动互联网,金融', '上海', '15k-30k', '5529012', '公司平台,晋升空间,弹性工作,节假日福利', '浦东新区', '数据分析', '岗位职责：1、数据提炼、分析、归纳用户属性、行为等信息，完成分析结果；2、处理用户海量数据，挖掘用户行为特征，为产品、运营提供参考依据；3、针对具体业务问题，研究影响用户的潜在因素，进行数据分析验证并提出改善举措；4、参与各业务部门的重点项目，负责从数据的角度给出决策建议；5、能实现业务所需的数据分析的应用系统开发。任职条件：1、 本科及以上学历，计算机、统计、经济、数理、信息等相关专业，2年以上相关工作经验，有互联网或金融行业数据分析经验者优先；2、 熟练掌握SQL、EXCEL、PPT、R等常用分析工具及相关编程语言；3、 熟悉多种数据源深度诊断性组合分析、挖掘和建模、数理统计、数据分析、数据挖掘以及常用算法；4、 精通数理统计，掌握常用数据挖掘方法，例如：线性回归，逻辑回归，决策树分群，聚类、关联分析、SVM，贝叶斯等；5、 熟悉Hadoop、Hive、Spark或其它开源数据挖掘（Clementine、SPSS/SAS）或机器学习等项目，能够解决数据分析过程中遇到的问题；6、 对商业和业务逻辑敏感，具备良好的逻辑分析能力和系统性思维能力，优秀的数据思维和强烈的数据决策意识，且思维缜密，做事认真严谨；7、 有较强的人际沟通、协调能力，具备与相关人员沟通数据需求的能力；8、 银行、信贷、电商、互联网行业数据挖掘分析经验或商业分析应用者优先。');
INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('智能语音-深度学习研究员', '经验3-5年', '硕士及以上', '上市公司', '搜狗', '移动互联网,数据服务', '北京', '20k-40k', '3023153', '海量数据,超算平台,畅游人工智能时代', '海淀区', '深度学习', '【项目介绍】在智能硬件设备中，语音已经成为一种最重要的用户交互方式。智能语音通过将语音识别、语义理解和语音合成技术相整合，让智能设备具有了与用户自然对话，理解用户意图并完成用户需求的能力。搜狗智能语音项目致力于通过不断提升语音和语义方面的技术能力，让用户的交互过程更智能体验更流畅；也致力于不断提升自己连接搜索和垂直行业资源的能力，更好的满足用户的多样化内容消费需求。同时搜狗会着力打造业界最开放技术能力最强服务接入资源最多的智能语音开放平台，把能力最大限度的开放给具有智能交互梦想的合作伙伴，让更多的用户感受到搜狗人工智能的魅力。【特别提示】搜狗欢迎专情的你，所以提醒你只能选择两个项目，请慎重投递。【岗位职责】1、跟进并搭建面向企业级的大规模深度学习训练平台系统；2、基于深度学习训练平台为语音/图像等应用提供模型支持任职条件1. 数学/计算机/模式识别等相关专业，硕士及以上学历；2. 熟悉常见的深度学习网络模型（FFNN/RNN/CNN等），有一定的实践和优化经验；3、至少熟练使用一种脚本语言（如python或perl/shell等），有一定的C++开发经验；4、至少熟悉一种深度开源训练框架（如TensorFlow或Caffe/MXNet等）；5、有一定的分布式框架系统搭建经验者、有MPI/CUDA/FPGA等经验者更佳；6、对语音识别领域有过深度模型优化经验者更佳； 7、具备较强的逻辑思考能力、沟通能力、学习能力、有责任心，抗压性能力强');
INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('高性能深度学习库开发工程师', '经验3-5年', '本科及以上', 'A轮', '寒武纪科技', '硬件,数据服务', '北京', '20k-30k', '5537849', '绩效奖金 六险一金 工作居住证 弹性工作', '海淀区', '深度学习,图像处理', '高性能深度学习库开发工程师岗位职责：1. 基于寒武纪深度学习芯片以及硬件平台，开发高性能深度学习库。2. 与芯片以及硬件平台设计人员配合，对高性能深度学习库进行需求分析。3. 对寒武纪高性能深度学习库进行性能分析与优化。4. 以高性能深度学习库视角，对寒武纪深度学习芯片以及硬件平台提出优化建议。职位要求：1. 本科及以上学历，数学、物理、计算机、微电子、通信、自动化等相关专业。2. 精通高性能数学库开发，mpi编程。3. 精通linux环境下C++编程。4. 熟悉脚本语言，cuda编程。5. 熟悉深度学习算法者优先。6. 熟悉编译优化相关技术者优先。7. 具有良好的理解能力和英语阅读能力。8. 逻辑思维强，做事踏实、认真，具备一定抗压能力。9. 具有良好的团队协作精神，责任心强，能够积极主动地完成相关工作。');
