INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('机器学习', '经验不限', '本科及以上', 'D轮及以上', '好大夫在线', '医疗健康', '北京', '25k-50k', '4531121', '大公司,大平台,机会多,空间大', '朝阳区', '人工智能,机器学习,深度学习,图像处理', '工作职责：1. 用机器学习、文本挖掘等技术，对结构化和非结构化数据进行挖掘，发现其潜在关系，指导医疗大数据应用落地；2. 构建通用的分布式环境下机器学习系统，快速支持算法应用；3. 参与文本意图分析，包括文本分类和聚类，拼写纠错，实体识别与消歧，中心词提取，短文本理解等；4. 探索医疗AI领域。工作要求：1. 计算机/通信等相关专业，硕士及以上学历，有较好的理论基础和快速的学习能力；2. 熟练掌握TensorFlow，pyTorch，Caffe，HTS等社区开源工具中的一种及以上；3. 对常用的机器学习/深度学习算法有深入理解，例如：LR/GMM/SVM/CRF/MaxEnt/HMM/NN 等；4. 熟悉 linux 开发环境，掌握至少一门编程语言，如 C++、Python等；5. 有NLP或图像领域的工作或研究经验，搭建过实际线上系统者优先；6. 表达能力强，乐观向上，自我驱动力强，具备敏锐的洞察力和良好的抗压能力。');
INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('深度学习算法工程师', '经验1-3年', '本科及以上', '天使轮', '莱尔托特', '移动互联网,电子商务', '深圳', '15k-30k', '5551252', '福利好,待遇高,六险一金,下午茶', '南山区', '电商,大数据', '工作职责:1、负责深度学习及机器学习相关算法的开发和应用；2、负责算法相关的数据处理。任职资格：1、熟悉机器学习的基础理论和常用算法；2、熟悉tensorflow，pytorch等深度学习框架；3、熟悉linux系统，熟悉Python语言；4、熟悉逻辑回归、随机森林、决策树、贝叶斯、SVM等基础分类算法；5、熟悉CNN、RNN等人工智能算法及其原理；6、熟悉有监督学习、无监督学习等原理及实现；7、对数据敏感，具有良好的沟通能力和团队合作精神，具有强烈的责任心和钻研精神。');
INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('视觉跟踪/深度学习工程师', '经验1-3年', '本科及以上', '天使轮', '睿魔智能', '硬件,其他', '杭州', '15k-25k', '4573093', '前沿技术,富有挑战,福利多多', '西湖区', '人工智能,机器学习,计算机视觉,模式识别', '岗位描述：1.研究并实现业界领先的实时跟踪算法2.研发速度更快/效率更高的跟踪算法3.完成算法在PC以及嵌入式平台上的部署工作任职要求：1.扎实的图像分析和模式识别理论基础2.对业界state of art的visual tracking算法有深入了解3.对基于相关滤波（CF）或CNN的跟踪算法有实现经验4.熟悉基本的计算机视觉任务，如目标检测/分割，或目标跟踪与识别等5.掌握基本的C++，或熟练使用Matlab进行开发6.对深度学习有强烈兴趣，熟悉相关深度学习框架者加分');
INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('深度学习算法工程师(实习）', '经验应届毕业生', '硕士及以上', 'A轮', '脑医生', '医疗健康,数据服务', '深圳', '3k-5k', '5355954', '高端前沿,前景广阔,年终奖金', '南山区', '医疗健康,人工智能,深度学习', '岗位职责：1、与公司国内外AI团队一起负责机器学习、深度学习相关算法研发；2、负责公司相关医疗AI产品的实现和专利论文的编写；3、维护与优化公司脑医生云平台；技能要求：1、硕士及以上学历，计算机及相关理工科专业；2、2年以上python开发经验，有扎实的数学基础，熟悉机器学习、深度学习相关算法；；3、熟悉TensorFlow, Keras, Caffe, PyTorch等常用深度学习框架至少一种；4、有使用python进行数据分析的经验，熟练使用Python常用库和工具，比如pandas，numpy，matplotlib等；5、有良好的英文文献阅读能力，适应全英文开发环境（包括英文论文／技术文档，英文交流，英文注释等）；6、善于思考，能提出高质量的问题，沟通能力良好，善于团队合作。有以下条件者优先考虑：1、有使用python进行deep learning数据分析的经验，熟悉CNN，R-CNN；2、能够熟练使用腾讯云或者阿里云资源，例如：云服务器、信息安全、IO、存储和网络产品；3、有分布式系统开发和维护经验；4、具有计算机视觉以及数据分析专业背景；5、211、985以及国外名校计算机相关专业毕业并具备相关经验；');
INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('深度学习算法工程师', '经验3-5年', '硕士及以上', '未融资', '趣途科技', '移动互联网,教育', '深圳', '20k-35k', '5757016', '专业产品团队,专业项目管理,资深教育专家', '南山区', '深度学习,图像处理,建模', '岗位职责：深度学习算法的研究和应用，包括以下方向：1. 实时人体/表情捕捉, 3D动画 （手部/脸部/全身）；2. 单目RGB摄影机图像/视频的人体3D转换（手部/脸部/全身）；3. 语音交互，手势交互；4. 使用少数量多视角照片的3D建模（一般物体）；5. 用户能控制的3D人脸生成（Generative Model）；6. 可以生成各种人声音的Text to speech model；7. Speech Animation（脸部）；8. 人体运动分析（Kinematics/Dynamics, Action recognition）；9. 贴图/材质生成，单张图像的BRDF推测；10. 虚拟试衣之类的布料物理运算；有以上某一个方向实际项目经验者优先；任职资格：1.  计算机视觉，人工智能，深度学习等相关方向硕士及以上学历；2.  熟练掌握C/C++，Python&Shell等脚本语言，熟悉Linux开发环境；3.  熟练使用Tensorflow、Caffe、PyTorch、MxNet等任一种深度学习开源框架；4.  熟悉常见的深度学习算法，如CNN，RNN，LSTM等；5.  熟悉神经网络模型的设计、调参、优化方法；熟悉模型压缩、算法性能优化；6.  有深度学习项目大规模样本训练、调优、应用经验者优先；7.  CVPR, ICCV, ECCV等会议发表过论文者优先；');
