INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('机器学习', '经验1-3年', '本科及以上', 'B轮', '数美', '企业服务,数据服务', '北京', '20k-40k', '3927473', '飞速发展,大佬领投,顶级团队,海量数据', '朝阳区', '广告营销,金融,NLP,算法', '1、基于数美海量数据进行数据建模与分析2、对海量数据进行feature selection, combination等feature engineering工作 3、针对海量用户行为数据，构建用户兴趣属性，金融属性画像，欺诈属性等多维度画像 4、基于海量画像数据，构建金融风控，互联网反作弊，精准营销，垃圾内容等模型和服务1、硕士及以上学历，机器学习或统计学习建模领域拥有实际工作经验；2、良好的逻辑思维能力，能够从海量数据中发现有价值的规律，熟悉大规模数据挖掘、机器学习等基本算法；3、编程基础扎实，熟悉算法数据结构，有超过2年以上C++或Python开发经验  4、有金融模型、广告算法、推荐算法等相关领域研究或实践经验者优先；5、 踏实勤奋，自我驱动   有竞争力的薪酬期权奖励五险一金+商业保险午餐+晚餐补助交通+通讯+电脑补贴结婚+生育+丧葬+住院礼金伯乐奖金年假8天起+带薪病假年度体检、零食畅享、团队建设、生日会、弹性办公      数美由百度、小米、360等顶尖互联网公司联合投资的大数据公司，致力于利用人工智能技术和海量数据解决金融、互联网等领域广泛存在的欺诈问题，先后推出了信贷反欺诈、内容反欺诈、行为反欺诈等系列产品，           目前已服务数百家客户，覆盖直播、金融、支付、社交、电商、游戏、O2O等行业。其中包括中信银行、360、小米、58同城、爱奇艺、酷狗、用钱宝、点融、挖财、闪银、熊猫TV、花椒、唱吧等知名企业，并与腾讯云、金山云、七牛云等云服务提供商展开深度合作。    数美的创始人唐会军在搜索、安全、语音识别等大数据领域拥有十余年研发经验，曾历任百度系统技术负责人、360高级技术总监等职位。创始团队均来自百度、360、小米、宜信、FICO等知名互联网公司，数美核心团队是国内最早一批从事大数据平台与应用研发的团队，曾负责过的大数据平台规模居全球前十。国际顶尖反欺诈技术，拥有十余项专利，同时在安全，反作弊、推荐，广告，语音识别等领域有着丰富的大数据成功应用经验。           www.ishumei.com');
INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('PTBU-深度学习工程师', '经验1-3年', '本科及以上', '上市公司', '酷狗音乐', '移动互联网,文化娱乐', '广州', '20k-30k', '5782451', '餐饮补贴 大平台 五险一金', '天河区', '音乐,直播,人工智能,机器学习,深度学习', '工作职责:1.负责实时个性化推荐系统的设计、搭建与优化，包括模型在线训练、实时排序、特征计算、监控分析等模块的开发部署； 2.负责个性化推荐产品需求的开发与调优。任职资格:1.精通Linux环境下的代码开发，熟练使用调试工具，有推荐系统或广告系统研发经验；2.具备良好的编程能力和代码风格，熟练掌握一门强类型语言，并至少掌握一门脚本语言3.熟练掌握机器学习、深度学习、强化学习等方向理论和应用，有实际算法调优经验优先；4.熟练应用算法平台之一，如TensorFlow、PyTorch、MXNet等，有模型在线serving部署经验加分； 5.有大型机器学习、深度学习项目经验优先，有推荐算法项目经验优先。');
INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('深度学习算法工程师（无人驾...', '经验3-5年', '本科及以上', '未融资', '成都威玛', '招聘', '北京', '20k-40k', '5754155', '五险一金,面试后可谈期权。', '昌平区', '深度学习', '主要职责1、负责深度学习视觉算法在自动驾驶中的探索、应用与优化；2、总结研究成果，在相关***国际学术会议发表论文；任职资格1、拥有扎实的数学功底，对深度学习方法、计算机视觉等领域有深入研究和独到见解；2、较强的编程能力，熟悉C/C++、Python；3、精通Caffe, Torch等框架至少其中之一，有毫米波雷达经验优先；4、有能力阅读最新学术论文，并能够编程实现；5、具备创新精神，不断挑战算法能力的边界；6、具有良好的团队合作意识、沟通能力、协调能力；（北京上海均有岗位需求）');
INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('机器学习/深度学习算法工程师', '经验1-3年', '硕士及以上', 'A轮', '深思考人工智能机器人', '移动互联网,企业服务', '北京', '15k-30k', '5536485', '弹性工时、核心岗位、AI', '海淀区', '其他,机器学习,深度学习,建模,算法', '岗位职责：1．结合数据挖掘传统算法，机器学习浅层算法，深度学习深层算法在人工智能领域的产品中进行分类和预测；2．以上算法在图像识别、自然语言处理、舆情分析、语音识别等人工智能方面的应用；3．以上算法在高性能云计算服务器上的并行化和优化工作。任职要求：1．硕士及以上学历，计算机相关专业毕业；2．对机器学习的一般模型如分类、聚类、关联分析、预测等模型熟悉（LR，CART，SVM，NN，Bayesian，PCA，LDA等），理解一些常用的特征选择和矩阵分解的算法；3．熟悉深度神经网络和常用的模型,如RBM、CNN、DBN、Sparsecoding、RNN等，对常用的开源实现,如Caffe、Theano或ConvNet等有实践经验者优先考虑；4．熟悉深度神经网络在图像识别、自然语言处理方面的应用，有文本处理方面的经验者优先考虑；5．熟悉大规模并行计算的基本原理并具有实现并行计算算法的基本能力，熟悉Hadoop或者Spark架构者优先考虑，有过基于GPU指令的算法优化经验者优先考虑；6．良好的英文阅读能力，跟踪学术界和工业界的最新研究成果，并可以通过编程语言（C、C++、java、Python、Matlab）来编程实现，有编程实现过近几年深度学习相关论文算法者着重考虑；7．有创造性的思维，敢于超越技术极限，致力于研发一流的技术和产品；8．具有良好的沟通能力，和良好的团队合作精神。');
