INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('深度学习算法工程师（实习生）', '经验应届毕业生', '本科及以上', '天使轮', '宜远智能', '医疗健康,数据服务', '深圳', '4k-8k', '4160203', '大神指导,福利多多,员工宿舍,转正机会', '南山区', '广告营销,机器学习,深度学习,计算机视觉,图像处理', '【岗位职责】负责机器学习、图像识别的算法和系统开发，如目标检测和分类模型的开发及效果优化。【任职要求】计算机相关专业，精通或熟悉深度学习相关算法；具备较强编程能力，熟悉Pytorch, MxNet, Tensorflow, Caffe等深度学习框架中的一种，跑过faster rcnn等模型；具备计算机视觉，图像处理，深度学习算法实现相关经验；985/211大学硕士、博士在读者优先；实习时间至少3个月，每周到岗4-6天，full time最佳。【你能得到的】来自腾讯，最好计算广告点击率预估团队之一的算法工程师，深度学习算法指导；来自香港浸会大学的GPU高性能计算实验室的博士团队，高性能计算算法指导。【温馨提示】此岗位为实习岗位，视表现有机会获得正式offer；工作地点深圳南山，如不能保证两个月内入职请勿投递，谢谢。公司可协助安排不超过四个月的宿舍，房租远低于市场价。');
INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('深度学习算法工程师', '经验1-3年', '硕士及以上', 'A轮', 'homi', '移动互联网,硬件', '北京', '15k-30k', '4907088', '弹性工作制,boss好,股票期权,福利好', '朝阳区', '智能硬件,爬虫架构,机器学习,Java,C', '岗位职责：1、负责参与项目需求评审，参与数据标准建立；2、维护并优化已有的学习模型；3、深度学习算法研究、实现和优化，包括但不限于光线学习、用户行为识别等方向；4、负责产品落地时与开发人员和产品经理的对接，涉及算法优化、平台移植等工作，负责模型训练与效果评估；5、负责对现有数据进行数据挖掘与分析；6、编写开发文档。任职要求：1、硕士及以上学历，数学、应用数学、统计学、计算机等专业，有3年及以上模型算法应用经验；2、熟悉常见爬虫框架和算法包，有扎实的代码（Python/Java/C）开发能力；3、精通各种机器学习算法及其原理，能根据业务场景，建立挖掘模型，选择最合适和高效的算法并实现；4、有丰富的数据建模、模型评估、模型优化、模型工程化及模型应用能力并有相关的实际项目经验；5、具备良好的逻辑分析能力，较强的独立思考和解决问题的能力，面对模糊的业务场景，能够理清思路，提出清晰解决方案；6、具备良好的沟通表达能力、团队合作精神，有较好的产品应用规划设计能力者优先；7、思维活跃，能快速学习新知识，对算法研发和优化富有热情。');
