INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('深度学习算法工程师', '经验应届毕业生', '硕士及以上', 'A轮', '聚力维度', '人工智能', '北京', '7k-8k', '2985329', '影视智能,背靠巨头,清华合作,科研文化', '海淀区', '人工智能,机器学习,深度学习,模式识别', '工作职责：1.负责深度学习在计算机视觉领域的应用；2.在团队积累的基础上，进一步优化参数，改进数学模型和损失函数；3.与我们整个科幻成真实验室一起进行发散式科研，一步步实现通用人工智能。岗位要求：1.模式识别或人工智能相关专业硕士/博士毕业；2.至少一年的人工智能图形/图像/音频领域项目研究经历；3.至少熟悉PyTorch，TensorFlow，MxNet，Caffe等一种深度学习开源框架；4.时刻对深度学习相关领域最新进展和最新思想深入掌握；5.了解自然语言领域和强化学习领域的技术思想；6.有实现通用性人工智能梦想者优先。');
INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('机器学习', '经验1-3年', '本科及以上', 'D轮及以上', '闪银奇异', '移动互联网,金融', '北京', '20k-35k', '4318100', '全额公积金,自助午餐,高新技术,大牛领导', '朝阳区', '大数据,互联网金融,算法,数据挖掘,数据处理', '岗位内容:1、用户行为数据挖掘与特征研究，基于业务场景及海量行为数据发现客户行为规律，识别业务风险问题；2、对于互联网海量业务数据的进行大规模特征提取和分析挖掘；3、提供高可靠的特征工程，保证高可靠的线下模型训练和线上模型打分。4、基于客户风险和海量行为数据，设计并开发实现风控打分卡。任职要求1、统招本科及以上学历，2年及以上大数据处理，机器学习相关工作经验；2、精通spark，熟悉hadoop生态常用技术；3、熟悉Java/Scala/Python至少一门编程, 掌握面向对象编程思想和常见的设计模式；4、对算法和数据结构有深刻的理解, 编码能力扎实；5、熟练使用hive、sparkSQL、hbase6、对分布式机器学习平台感兴趣者优先；7、思路清晰，主动性强，有很强的自我驱动能力');
INSERT INTO job_dl (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('深度/机器学习', '经验不限', '硕士及以上', 'B轮', '深睿医疗', '移动互联网,医疗健康', '北京', '25k-50k', '3813295', '大牛,人性化管理,靠谱团队,技术氛围好', '海淀区', '医疗健康,机器学习,深度学习,计算机视觉,算法', '研究深度学习（包括各种神经网络结构与应用）或计算机视觉各个领域（目标检测识别等）中核心算法将上述核心算法和模型应用到医疗领域的现实场景中，并针对应用场景进行优化和定制等将算法高效地实现到多种不同平台和框架上，并基于对平台和框架的内部机制的理解，持续对算法和模型实现进行优化支持公司在医疗领域相关应用产品的研究、开发、文档撰写工作；岗位要求：机器学习/模式识别及其他相关领域硕士及以上学历，在深度学习领域有较深的理论或实验室研究经验良好的算法基础，扎实的编程功底，有较大型项目的开发经验能够熟练使用OpenCV，精通C/C++，Python,golang等，熟悉Linux开发环境善于解决和分析问题，富有想象力和学习能力，良好的团队合作精神，有创造性思维，有推进人工智能的理想和使命感。有以下经验者优先：具有常用的CNN/RNN模型之外深度学习相关经验，熟悉常用的深度学习软件（如caffe，pytorch等）在相关方向知名国际会议发表过论文（如CVPR，ICCV，ECCV，NIPS，ICLR，TPAMI等）参加过国际国内算法类竞赛，并取得一定名次者');
