INSERT IGNORE INTO job_pd (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('高级产品经理', '经验5-10年', '本科及以上', 'B轮', '微问家', '移动互联网', '上海', '20k-30k', '4946925', '五险一金,年终奖金,奖金提成,弹性工作', '浦东新区', '产品设计', '岗位职责：1、负责中小企业CRM及营销端产品的全过程管理，包括与渠道/销售/市场部门以及合作客户沟通，收集并确认客户/用户需求；2、负责业务线产品策划、设计，负责面向客户的产品相关的文档撰写；3、进行产品上线测试、推介演讲、使用培训等相关执行工作；4、负责具体功能的设计，不断优化产品体验，并协调/推动研发团队按时完成产品开发、高质量上线；5、分析运营数据，并能持续改进、完善产品以提高产品的用户体验和用户留存；任职要求：1、本科及以上学历，3年以上产品设计经验；2、有SaaS或B2B产品经验，熟悉CRM、HR、OA等一个或多个模块；3、逻辑思维能力强，有良好的团队协作意识，具备优秀的沟通能力；4、同理心强，能耐心倾听，沉心研究用户的需求，至少精通一样产品工具；5、对中小企业市场很有兴趣，具有较强的执行力和责任感，能接受高强度的工作；');
INSERT IGNORE INTO job_pd (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('产品经理', '经验1-3年', '本科及以上', 'B轮', '商帆', '移动互联网', '上海', '10k-20k', '5162494', '平台大,晋升空间大,管理扁平化,地铁周边', '徐汇区', '新零售,云计算,产品设计,需求分析', '岗位职责： 1、结合市场以及公司实际情况，执行公司运营策略，完善产品的需求分析、产品设计 ；2、准确把握用户的管理理念，创新性地设计客户生命周期全过程产品 ，管理整个产品周期，包括概念、设计、开发、上线以及运营；3、负责产品业务规范，业务流程及相关内容设计，负责需求的整理跟踪及控制，确保产品按照计划进行；4、负责系统的易用性提升，对公司现有用户的使用意见进行搜集和积累，并进行分析，提炼出产品改进的方向和细节 ；5、负责公司系统及业务应用相关的产品知识总结、积累，并对试用的客户进行系统功能培训支持，便于扩大客户使用的区域；任职要求：1、计算机及相关专业或管理类专业,大学本科以上学历2、有IT行业背景；3、较强的用户需求判断、引导、控制能力，逻辑思维能力对客户关系管理产品有深入理解；4. 责任心强，抗压能力强，能接受加班，善于接受建议和反馈；5. 沟通及协调能力强，逻辑思维非常好，学习能力强，对新事物有强烈的好奇心； 6. 有强的商业嗅觉和产品化意识，能主动发起产品的改进和优化，有执行力。');
