INSERT IGNORE INTO job_pd (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('产品经理', '经验1-3年', '本科及以上', '天使轮', '星视度', '数据服务,广告营销', '上海', '12k-13k', '5805331', '待遇好,福利优,团队活力,重点项目', '浦东新区', '用户研究,产品策划,需求分析', '工作职责： 1、根据企业战略、用户需求、技术条件等，规划定义产品2、协调技术、销售、设计等各部门，对产品从概念、开发、测试、生产、发布的整个过程进行管理3、对产品进行跟踪，根据营销结果和用户反馈，持续改进产品4、配合销售与用户进行产品技术交流、订制方案设计等5、负责项目管理，监督项目的执行过程，控制项目进度和质量，沟通和协调资源，及时解决项目执行过程中遇到的各种冲突及问题6、 积极成为产品的核心开创者，引导并参与产品一系列的后续推广、创意构思及执行；任职要求:1、本科及以上学历，机械、电子、通信、工业设计、计算机或相关专业；2、3年以上产品经理工作经验，有需求管理，交互设计，项目管理经验；3、对新产品、新技术有敏锐的嗅觉，有很强的产品方案设计能力；4、抗压能力强、有较强责任心、主动性和创新意识；5、有Saas项目经验优先');
