INSERT IGNORE INTO job_pd (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('产品经理 (MJ001056)', '经验5-10年', '学历不限', 'C轮', '九曳供应链', '电子商务,数据服务', '上海', '20k-35k', '5525847', '双休+法定节假日+生日福利+节日福利', '嘉定区', '产品设计,用户研究,需求分析', '工作职责1、负责生鲜品B2B（国内、跨境）电商平台的产品设计工作，包括电商门户、在线询报价系统、订单执行、（国内、跨境）支付系统、（国内、跨境）物流系统、供应链金融等系统；2、进行需求调研、参与业务规划、负责业务流程设计、功能设计，负责项目落地，并对产品进行持续优化等工作；3、日常跟踪观察竞争对手产品、用户情况，定期进行分析。任职资格1、5年以上产品工作经验，熟悉电子商务、供应链、企业ERP、物流仓储管理及CRM任一领域；且具备电子商务平台、供应链、企业ERP、物流仓储、CRM类产品业务实操经验，曾策划并实施一个具有一定市场或行业影响力的产品，并具备经历整个产品生命周期的经验。2、精通产品实施过程，包括市场分析，需求分析，产品功能设计，业务流程设计，界面设计，用户研究和用户性测试等。');
