INSERT IGNORE INTO job_pd (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('产品经理', '经验3-5年', '本科及以上', 'D轮及以上', '轻轻家教', '移动互联网,教育', '上海', '14k-18k', '5764507', '团队氛围好；扁平化管理', '徐汇区', '产品策划', '工作职责:1.负责偏B端产品调研，沟通，落地，分析，优化的闭环工作2.需能有效与教学和策略运营团队沟通配合，合理高效的制定B端的产品实现；3.深入家长、老师用户，对用户深层需求有强烈的好奇心，根据用户调研和数据分析进行产品判断，,对最终的用户体验负责；4.负责跨部门沟通，协调相关项目团队共同达成产品目标。任职资格:1.本科及以上学历，2年左右相关产品经理工作经验，对教育行业感兴趣；2.对用户需求有敏锐的洞察力，并具备出色的沟通能力、项目把控能力、产品策划和执行能力、文档表现能力、数据分析能力；3.有激情，责任心强，善于团队合作，学习能力优秀，沟通力佳，抗压能力强；');
