INSERT IGNORE INTO job_pd (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('产品经理', '经验1-3年', '本科及以上', 'A轮', '长投学堂', '移动互联网', '上海', '15k-25k', '5799827', '季度旅游,年终奖,投资技能,团队年轻', '浦东新区', '产品设计', '岗位职责：1、负责现存流量入口提升订单转化率或了解/熟悉工具类开发；2、探索新的流量入口，勇于创新，从数据出发，发现新的产品增长点或改进点，并提出切实可行的产品方案；3、收集内外部用户需求、进行市场分析、产品规划、设计产品功能、项目管理等；4、负责与各部门配合沟通、资源协调，从概念设计、开发、上线、内外部实施如期顺利完成；5、跟踪功能效果，分析产品、用户体验、满意度与转化率等核心指标；6、密切关注市场动态，竞品分析、提升产品竞争力；7、配合运营团队策划活动，协助推动品牌与市场建设。任职要求：1、本科及以上，2年以上移动产品相关工作经验；2、较强的同理心和用户思维，能从用户的视角思考问题；3、对移动互联网产品的交互设计、用户体验提升、功能易用性等有成熟经验优先；4、对时事热点和网络热点极度敏感，能第一时间发现最新动态、善于分析用户需求、熟练使用网络产品设计软件;5、了解新媒体平台的运营特点和规则优先；6、自我驱动力强，能根据不同任务需求，及时反馈和调整内容方向。');
INSERT IGNORE INTO job_pd (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('旅游产品经理（非it研发）', '经验3-5年', '学历不限', 'A轮', '斑马旅游', '电子商务,旅游', '上海', '10k-20k', '5711938', '发展空间大,氛围轻松,弹性工作,员工聚餐', '浦东新区', '旅游', '职位描述：【岗位职责】：1、分析用户需求及市场竞争情况，研发特色旅游产品。2、独立开发、筛选、维护出游供应商，完成产品采购、合作洽谈、合同管理、后期维护等工作。3、精选目的地资源，负责旅游目的地当地产品的调研与采购，进行线路规划。4、负责旅游新产品的包装策划、设计并提交整个产品服务标准及检测。5、根据销售数据与客户反馈，制定和提供产品改善策略，推动产品更新与迭代。【任职要求】：1、全日制本科以上学历，有境外旅游行业相关经验优先。2、5年以上工作经验，有OTA、传统旅行社、供应商等旅游行业工作经验优先考虑。3、能够独立开发新业务和供应商，分析规划旅游度假产品架构,国内外旅游专业知识丰富，有一定的供应商资源基础优先。4、对客户需求有敏锐的把握。5、有较强的逻辑分析和数据分析能力，擅长沟通协调，学习能力佳，抗压性强。6、热爱旅游，致力于旅游行业发展。7、具备英语或小语种能力者优先。   工作地址');
INSERT IGNORE INTO job_pd (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('产品经理（通信/电信、联通、...', '经验5-10年', '大专及以上', 'B轮', 'MDC', '移动互联网', '上海', '15k-25k', '5807036', '五险一金 商业保险 发展空间大', '杨浦区', '即时通讯,产品策划,产品设计,用户研究', '1、负责公司和运营商业务相关的产品规划、业务流程设计、功能设计、产品优化、推广运营等工作；2、编写完整的产品需求文档，协助开发团队理解和掌握需求，对产品需求方向和易用性负责；3、完成从产品设计、交付开发到正式上线的项目管理全过程；4、跨部门协调和沟通，推动市场、技术、运营等人员紧密合作达成产品目标；5、收集产品需求，对市场需求和业务需求进行调研分析，不断优化产品，提升产品质量。3、良好的用户体验感和数据分析能力；4、优秀的沟通和表达能力，逻辑思维能力强，责任感强，工作积极主动有激情，有良好的团队协作意识；5、较强的执行力，能承受一定压力并高效工作。福利：五险一金、绩效奖金、节日福利、带薪年假、扁平管理、技能培训、员工旅游、弹性工作。');
