INSERT IGNORE INTO job_pd (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('数据产品经理', '经验3-5年', '本科及以上', '不需要融资', '云鹊医疗', '医疗健康,移动互联网', '上海', '20k-30k', '5739657', '年底双薪 年终奖金 补充商业险', '浦东新区', '医疗健康,移动互联网,用户研究,需求分析,数据分析', '参与公司产品的数据分析、用户分析、内容分析等分析工作；为公司产品用户进行用户分类，匹配标签；设计数据产品结构、可视化平台建设，协调开发资源，推动产品的开发和实施，保证项目如期上线；深刻理解业务目标，跟进产品上线进展，通过用户反馈及统计、分析业务数据，发现问题并优化改进产品；定期对行业政策、法律法规，市场、竞品等进行信息收集、分析。 职位要求：了解数据产品模式, 有开阔的产品视野和数据分析能力，对内容产品有一定洞察力，能够清晰规划数据产品的定位、策略及价值；负责数据型产品的数据收集、数据清洗、特征挖掘的策略制定；了解用户的需求调研、需求分析的一般流程及方法；熟悉互联网或软件产品整体项目实现过程，能够独产承担产品规划设计工作；全日制本科及以上学历，优先考虑生物学/医学/计算机/数学/统计学相关背景专业。');
