INSERT IGNORE INTO job_pd (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('产品经理（体育）', '经验1-3年', '大专及以上', '不需要融资', '雷速体育', '移动互联网,社交网络', '上海', '8k-16k', '2010534', '轻松办公环境、无限零食、桌球、乒乓球', '虹口区', '产品策划', '岗位职责：1、对互联网及移动互联网的产品有较深了解，负责产品的整体规划；2、对产品数据进行统计及分析，根据数据调整运营策略及协同产品经理进行产品调整，做好产品支持和制定运营计划；3、对产品的交互及体验进行改进；4、推进产品优化升级，对目前的产品提出可行性的修改及创新意见；5、能够独立完成产品策划方案，并落实制定运营流程、规范。任职要求：1、熟悉体育项目及产业2、有2年以上互联网运营经验，1年以上产品经验；3、熟悉用户心理，制订可行的产品方案并有效的执行，对相关策略有独到见解的优先；4、有一定的数据分析能力和语言、沟通、组织协调能力；5、具备较强责任心和团队精神，乐于接受挑战，勇于突破创新，工作认真细致，善于调动团队积极性，主动积极推动达成业务目标；6、对彩票、体育数据有一定了解者优先。');
