INSERT IGNORE INTO job_pd (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('产品经理', '经验3-5年', '学历不限', 'D轮及以上', '途虎养车网', '移动互联网', '上海', '30k-50k', '5648317', '5A办公室,公司氛围好,发展空间大', '闵行区', '产品设计,产品策划,用户研究', '『职位描述』1. 准备PRD并设计产品原型，评审通过后交付开发团队开发；2. 负责和业务部门的沟通需求，对需求准确性负责；3. 负责或配合其他部门制定产品运营计划，分析数据，持续改善产品；『岗位要求』1. 5年以上产品经理、项目管理相关经验；2. 熟悉互联网或软件产品整体实现过程，包括从业务调研、需求分析到产品发布；3. 对用户体验和工具效率优化有较深入理解；4. 较强沟通能力和团队精神，愿意加入创业团队，能承受较大的工作压力；5.做过内部工具，后台工具的更佳；6. 热爱互联网行业，对自己的成长有较高要求，不但注重结果，而且在工作中也会时时关注过程。');
INSERT IGNORE INTO job_pd (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('产品经理（创新广告方向）', '经验3-5年', '本科及以上', 'C轮', '收钱吧', '移动互联网', '上海', '15k-30k', '5728381', '平台大,晋升空间大', '虹口区', '广告营销,大数据,用户研究,广告,产品设计', '工作职责:负责收钱吧日千万交易流量的广告形态创新，设计各类广告形式、分析行为数据，持续优化；自助广告竞价系统的设计规划，持续优化；与销售、运营、广告主等多方紧密配合，完成产品需求调研、规划、设计和实现；与研发团队紧密配合，推进产品开发落地；产品上线的验收、培训、生产监控、数据分析和优化； 任职资格:三年以上工作经验，一年以上互联网广告产品工作经验；熟练使用Axure、Visio等产品工具；沟通良好、执行力佳、逻辑清晰、适度分析、了解技术实现基本概念；有好奇心；具备优秀数据分析能力，能通过工具独立完成数据分析的，优先考虑；');
INSERT IGNORE INTO job_pd (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('产品经理', '经验5-10年', '本科及以上', '上市公司', '源耀生物', '其他', '上海', '15k-25k', '5380021', '五险一金、旅游、年终奖金、体检、包吃住', '浦东新区', '电商,物流,需求分析,交互设计,项目管理,供应链', '岗位职责：1、负责手机APP和WEB前端等产品的需求制定，功能策划，产品原型设计；2、与设计、开发团队协作，按计划完成产品上线目标；3、负责用户研究，通过需求调研推进产品改进计划，提升产品质量和用户满意度；4、撰写详细需求文档；5、监控产品运行效果，收集用户反馈，分析用户行为统计数据，挖掘并发现用户需求，对产品进行持续改进；6、协调开发人员进行开发工作， 配合质量部门进行测试。任职要求：1、本科及以上学历，3－5年及以上相关工作经验；2、熟悉移动互联网行业，对产品有敏锐的感觉、领悟力强、思维活跃，善于创造性的解决问题；3、了解用户体验设计学，有审美能力，熟悉产品线流程图、产品需求文档(PRD)及技术功能描述文档的撰写——对产品的整个生命周期有深刻的理解；4、具备较强的逻辑分析能力、语言表达能力和写作能力，善于利用数据对用户需求及行为、产品发展等进行深入分析；5、有活力和激情，有很强的创新能力和好奇心，善于学习国内外互联网的最新成果，并能很好的在实践中应用；6、责任心/主动性强，有良好的团队合作精神，能承受工作压力，有强烈的责任心和主人翁精神。');
INSERT IGNORE INTO job_pd (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('商业产品经理', '经验3-5年', '本科及以上', '未融资', '上海你我贷互联网金融信息服务', '金融', '上海', '15k-25k', '5528577', '弹性工作制 奖金丰厚 领导NICE', '浦东新区', '产品设计,需求分析', '岗位职责：负责电话销售系统产品的需求调研、功能规划。2、与相关的产品经理、开发团队及业务部门保持良好的沟通，推动项目如期上线。 3、跟踪上线效果，收集需求并拟定优先级和迭代计划，持续优化产品功能和业务流程。 4、配合完成部门内其他各项工作。 5、需要双职场工作，每周3+2在总部和电销中心工作。任职要求：1、全日制本科以上学历，有从0到1搭建电销系统经验，熟悉客服及电销系统的业务运作。 2、3年及以上互联网产品设计经验，有1年及以上的电销系统产品经验。 3、善于沟通，逻辑清晰；有强烈的责任感和主人翁意识，团队合作意识强。 4、具备良好的文档撰写习惯，精通axure、visio、ppt等常用工具。外向性格，能够与电销、开发及同事们顺畅沟通。');
INSERT IGNORE INTO job_pd (position_name, work_year, education, finance_stage, company_short_name, industry_field, city, salary, position_id, position_advantage, district, position_labels ,job_desc) VALUES ('产品经理（店铺模块）', '经验3-5年', '本科及以上', '上市公司', '拼多多', '电子商务,移动互联网', '上海', '20k-40k', '5289966', '发展空间大,氛围好,扁平化管理,年底多薪', '长宁区', '产品设计', '1、负责拼多多商家侧店铺的产品规划、设计和实现，从店铺整个生命周期处罚，包括入驻流程、店铺风控、资质中心、保证金、子帐号和退店等。2、协调各方资源，组织和推动产品的设计及开发上线，并对产品质量进行把控。3、关注行业竞品、调研用户需求、收集用户反馈、分析效果数据，持续改善产品的用户体验。职位要求：1、本科及以上学历，3-5年互联网工作经验，有电商店铺产品经理工作经验优先；2、较强的逻辑思维能力和产品体验能力，熟练给出流程图、逻辑图和需求文档；3、有较强的商家理解能力，执行力较强并且有较强的抗压能力。');
